package d22;

import java.util.List;

import util.Task;

public class Day extends Task {

	String instructions = "";
	//String instructions = "10R5L5R10L4R5L5";
	int index = 0;
	private static int VOID = 0;
	private static int GROUND = 1;
	private static int WALL = 2;

	//PRINTA UT OCH SE NÄR DET GÅR FEL

	private enum Dir {
		RIGHT,
		DOWN,
		LEFT,
		UP,
	}

	public static void main(String[] args) {
		Day d = new Day();
		List<String> list = d.getStrings();
		d.p1(list);
		d.p2(list);
	}

	private String getNextInstruction() {
		if (index == instructions.length()) {
			return null;
		}
		String instr = "";
		char ch1 = instructions.charAt(index);
		index++;
		if (index == instructions.length()) {
			instr += ch1;
			return instr;
		}
		char ch2 = instructions.charAt(index);
		index++;
		instr += ch1;
		if (index == instructions.length()) {
			instr += ch2;
			return instr;
		}
		if (Character.isDigit(ch2)) {
			instr += ch2;
		} else {
			instr += "," + ch2;
			return instr;
		}
		char ch3 = instructions.charAt(index);
		index++;
		if (Character.isDigit(ch3)) {
			instr += ch3;
		} else {
			instr += "," + ch3;
			return instr;
		}
		char ch4 = instructions.charAt(index);
		index++;
		instr += "," + ch4;
		return instr;

	}

	private void p2(List<String> list) {

	}

	private void p1(List<String> list) {
		int[][] map = buildMatrix(list);

		int currentY = 0;
		int currentX = 50;
		Dir currentDir = Dir.RIGHT;

		int width = map[0].length;
		int side = width / 3;

		String instr = getNextInstruction();
		while (instr != null) {
			int steps = Integer.parseInt(instr.split(",")[0]);
			String turn = "";
			if (instr.split(",").length == 1) {
				turn = "";
			} else {
				turn = instr.split(",")[1];
			}
			boolean stopped = false;
			while (!stopped) {
				if (currentDir == Dir.RIGHT) {
					int nextX = 0;
					int nextY = currentY;
					boolean outSide = false;
					if (currentX + 1 > map[currentY].length - 1 || map[currentY][currentX + 1] == VOID) {
						outSide = true;
						int currentQuad = getCurrentQuadrant2(currentX, currentY, map[0].length);
						if (currentQuad == 2) { //next Quad = 5 högersidan upp och ned
							nextY = side * 2 + getPositionInverted(currentY, side, 1);
							nextX = side * 2 - 1;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.LEFT;
						} else if (currentQuad == 3) { //next 2 undersidan
							nextY = side - 1;
							nextX = side * 2 + getPosition(currentY, side, 2);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.UP;
						} else if (currentQuad == 5) { // next 2 högersidan
							nextX = side * 3 - 1;
							nextY = getPositionInverted(currentY, side, 3);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.LEFT;
						} else if (currentQuad == 6) { //next 5 undersidan
							nextX = side + getPosition(currentY, side, 4);
							nextY = side * 3 - 1;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.UP;
						}
					} else {
						nextX = currentX + 1;
					}
					if (map[nextY][nextX] == WALL) {
						stopped = true;
						break;
					}
					if (!outSide) {
						currentX++;
					} else {
						currentY = nextY;
						currentX = nextX;
					}
					steps--;
					if (steps == 0) {
						break;
					}
				} else if (currentDir == Dir.LEFT) {
					int nextX = 0;
					int nextY = currentY;
					boolean outSide = false;
					if (currentX == 0 || map[currentY][currentX - 1] == VOID) {
						outSide = true;
						int currentQuad = getCurrentQuadrant2(currentX, currentY, map[0].length);
						if (currentQuad == 1) { //next Quad = 4, vänstersidan 
							nextX = 0;
							nextY = side * 2 + getPositionInverted(currentY, side, 1);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.RIGHT;
						} else if (currentQuad == 3) { //next 4, toppen
							nextX = getPosition(currentY, side, 2);
							nextY = side * 2;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.DOWN;
						} else if (currentQuad == 4) { //next 1. vänstersidan
							nextX = side;
							nextY = getPositionInverted(currentY, side, 3);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.RIGHT;
						} else if (currentQuad == 6) { //next 1. toppen
							nextX = side + getPosition(currentY, side, 4);
							nextY = 0;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.DOWN;
						}
					} else {
						nextX = currentX - 1;
					}
					if (map[nextY][nextX] == WALL) {
						stopped = true;
						break;
					}
					if (!outSide) {
						currentX--;
					} else {
						currentY = nextY;
						currentX = nextX;
					}
					steps--;
					if (steps == 0) {
						break;
					}
				} else if (currentDir == Dir.UP) {
					int nextX = currentX;
					int nextY = 0;
					boolean outSide = false;
					if (currentY == 0 || map[currentY - 1][currentX] == VOID) {
						outSide = true;
						int currentQuad = getCurrentQuadrant2(currentX, currentY, map[0].length);
						if (currentQuad == 1) { //next 6, vänstersidan
							nextX = 0;
							nextY = side * 3 + getPosition(currentX, side, 2);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.RIGHT;
						} else if (currentQuad == 2) { //next 6. undersidan
							nextX = getPosition(currentX, side, 3);
							nextY = side * 4 - 1;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.UP;
						} else if (currentQuad == 4) { //next 3. vänster
							nextX = side;
							nextY = side + getPosition(currentX, side, 1);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.RIGHT;
						}
					} else {
						nextY = currentY - 1;
					}
					if (map[nextY][nextX] == WALL) {
						stopped = true;
						break;
					}
					if (!outSide) {
						currentY--;
					} else {
						currentY = nextY;
						currentX = nextX;
					}
					steps--;
					if (steps == 0) {
						break;
					}
				} else if (currentDir == Dir.DOWN) {
					int nextX = currentX;
					int nextY = 0;
					boolean outSide = false;
					if (currentY + 1 > map.length - 1 || map[currentY + 1][currentX] == VOID) {
						outSide = true;
						int currentQuad = getCurrentQuadrant2(currentX, currentY, map[0].length);
						if (currentQuad == 6) { //next 2 toppen
							nextX = side * 2 + getPosition(currentX, side, 1);
							nextY = 0;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.DOWN;
						} else if (currentQuad == 5) { //next 6. höger
							nextX = side - 1;
							nextY = side * 3 + getPosition(currentX, side, 2);
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.LEFT;
						} else if (currentQuad == 2) { //next 3. höger
							nextY = side + getPosition(currentX, side, 3);
							nextX = side * 2 - 1;
							currentDir = map[nextY][nextX] == WALL ? currentDir : Dir.LEFT;
						}
					} else {
						nextY = currentY + 1;
					}
					if (map[nextY][nextX] == WALL) {
						stopped = true;
						break;
					}
					if (!outSide) {
						currentY++;
					} else {
						currentX = nextX;
						currentY = nextY;
					}
					steps--;
					if (steps == 0) {
						break;
					}
				}

			}
			//TURN
			if (turn.equals("L") && currentDir == Dir.RIGHT) {
				currentDir = Dir.UP;
			} else if (turn.equals("L") && currentDir == Dir.UP) {
				currentDir = Dir.LEFT;
			} else if (turn.equals("L") && currentDir == Dir.LEFT) {
				currentDir = Dir.DOWN;
			} else if (turn.equals("L") && currentDir == Dir.DOWN) {
				currentDir = Dir.RIGHT;
			} else if (turn.equals("R") && currentDir == Dir.RIGHT) {
				currentDir = Dir.DOWN;
			} else if (turn.equals("R") && currentDir == Dir.UP) {
				currentDir = Dir.RIGHT;
			} else if (turn.equals("R") && currentDir == Dir.LEFT) {
				currentDir = Dir.UP;
			} else if (turn.equals("R") && currentDir == Dir.DOWN) {
				currentDir = Dir.LEFT;
			}
			instr = getNextInstruction();
		}

		System.out.println("x=" + currentX + " y=" + currentY);
//		sum of 1000 times the row, 4 times the column, and the facing.

		System.out.println(1000 * (currentY + 1) + 4 * (currentX + 1) + currentDir.ordinal());
	}

	private int getPositionInverted(int currentYorX, int side, int quadNumberFromTopOrLeft) {
		return (side * quadNumberFromTopOrLeft) - 1 - currentYorX;
	}

	private int getPosition(int currentXOrY, int side, int quadNumberFromTopOrLeft) {
		return currentXOrY - side * (quadNumberFromTopOrLeft - 1);
	}

	private int getCurrentQuadrant2(int currentX, int currentY, int width) {
		int side = width / 3;
		if (currentY < side) {
			//1 eller 2
			if (currentX < side * 2) {
				return 1;
			}
			return 2;
		} else if (currentY >= side && currentY < side * 2) {
			//3
			return 3;
		} else if (currentY >= side * 2 && currentY < side * 3) {
			//4 eller 5
			if (currentX < side) {
				return 4;
			}
			return 5;
		}
		return 6;
	}

	private int getCurrentQuadrantExample(int currentX, int currentY, int width) {
		int side = width / 4;
		if (currentY < side) {
			return 1;
		} else if (currentY + 1 > side * 2) {
			//5 eller 6
			if (currentX + 1 <= side * 3) {
				return 5;
			}
			return 6;
		} else {
			//2, 3 eller 4
			if (currentX + 1 < side) {
				return 2;
			} else if ((currentX + 1) <= side * 2) {
				return 3;
			}
			return 4;
		}
	}

	private int[][] buildMatrix(List<String> list) {
		int width = 0;
		int height = list.size();
		for (String row : list) {
			if (row.length() > width) {
				width = row.length();
			}
		}
		System.out.println("w" + width + " h" + height);
		int[][] map = new int[height][width];
		for (int y = 0; y < height; ++y) {
			String row = list.get(y);
			for (int x = 0; x < row.length(); ++x) {
				if (row.charAt(x) == ' ') {
					map[y][x] = VOID;
				} else if (row.charAt(x) == '.') {
					map[y][x] = GROUND;
				} else if (row.charAt(x) == '#') {
					map[y][x] = WALL;
				}
			}
		}
		return map;
	}
}
